    private void lstDeviceFocusGained(java.awt.event.FocusEvent evt) {
        Book[] bs = Book.listFromFolder(Config.GetInstance().getMPDEFolder());
        ShowFileDetails(bs[lstDevice.getSelectedIndex()]);
        lstLocal.clearSelection();
    }

    private void lstDeviceValueChanged(javax.swing.event.ListSelectionEvent evt) {
        if (evt.getValueIsAdjusting() == false) if (lstDevice.getSelectedIndex() >= 0) {
            Book[] bs = Book.listFromFolder(Config.GetInstance().getMPDEFolder());
            ShowFileDetails(bs[lstDevice.getSelectedIndex()]);
        }
    }

    private void btnOpenBookMouseReleased(java.awt.event.MouseEvent evt) {
        File f = new File(currentBook.getFileURL());
        try {
            open(f);
        } catch (Exception ex) {
        }
    }

    private void mniDeviceInfoMouseReleased(java.awt.event.MouseEvent evt) {
        JFrame did = new DeviceInfoDisplay();
        did.setVisible(true);
    }

    private void btnSetMountPointMouseReleased(java.awt.event.MouseEvent evt) {
        JFileChooser chooser = new JFileChooser();
        chooser.setCurrentDirectory(new java.io.File("."));
        chooser.setDialogTitle("choose mount point");
        chooser.setFileSelectionMode(JFileChooser.DIRECTORIES_ONLY);
        chooser.setAcceptAllFileFilterUsed(false);
        if (chooser.showOpenDialog(this) == JFileChooser.APPROVE_OPTION) {
            Config.GetInstance().setMountPoint(chooser.getSelectedFile().getPath());
            lblMountPoint.setText(Config.GetInstance().getMountPoint());
        }
    }

    private void btnRenameMouseReleased(java.awt.event.MouseEvent evt) {
        String strNewName = JOptionPane.showInputDialog(null, "Enter new book name: ", "Rename", 1);
        currentBook.setTitle(strNewName);
        currentBook.saveInfo();
    }

    private void btnNotesMouseReleased(java.awt.event.MouseEvent evt) {
        Mark[] ms = currentBook.getMarks();
    }

    private void ShowFileDetails(Book b) {
        currentBook = b;
        lblTitle.setText(currentBook.getTitle());
        lblAuthor.setText(currentBook.getAuthor());
        pbrRead.setMaximum(currentBook.getTotalPages());
        pbrRead.setValue(currentBook.getPageNumber());
        lblReadPages.setText((currentBook.getPageNumber() == -1 ? 0 : currentBook.getPageNumber()) + " / " + currentBook.getTotalPages());
        lblBookmarks.setText(Integer.toString(currentBook.getBookmarkCount()));
        lblNotes.setText(Integer.toString(currentBook.getNoteCount()));
        ImageIcon imgIcon = new ImageIcon();
        File imgFile = new File(currentBook.getImageURL());
        if (!imgFile.exists()) imgIcon = new ImageIcon(ClassLoader.getSystemResource("ebookmarks/resources/noimage.png")); else imgIcon = new ImageIcon(currentBook.getImageURL());
        btnOpenBook.setIcon(imgIcon);
        File f = new File(currentBook.getFileURL());
        long size = f.length();
        size = (size / 1024);
        lblFileSize.setText(size + " KiB");
        lblADENotes.setText(Integer.toString(currentBook.getAdobeNoteCount()));
        if (currentBook.getFileURL().startsWith(Config.GetInstance().getMountPoint())) lblLocation.setText("Device"); else lblLocation.setText("Local Drive");
    }

    public static void open(File document) throws Exception {
        try {
            Desktop dt = Desktop.getDesktop();
            if (dt.isSupported(Desktop.Action.OPEN)) dt.open(document);
        } catch (Exception ex) {
        }
    }

    @Action
    public void RefreshFiles() {
        lstDevice.setSelectedIndex(-1);
        lstLocal.setSelectedIndex(-1);
        modelListDevice.clear();
        String deFolder = Config.GetInstance().getMPDEFolder();
        Book[] bs = Book.listFromFolder(deFolder);
        for (Book b : bs) modelListDevice.addElement(b.getTitle());
    }

