package org.domain.tickethuntercrud.entity;

import java.util.HashSet;
import java.util.Set;
import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.FetchType;
import javax.persistence.GeneratedValue;
import static javax.persistence.GenerationType.IDENTITY;
import javax.persistence.Id;
import javax.persistence.OneToMany;
import javax.persistence.Table;
import org.hibernate.validator.NotNull;

/**
 * Place generated by hbm2java
 */
@Entity
@Table(name = "place", catalog = "tickethunter")
public class Place implements java.io.Serializable {

    private Integer id;

    private String name;

    private String description;

    private Set<Seat> seats = new HashSet<Seat>(0);

    private Set<EventDescription> eventDescriptions = new HashSet<EventDescription>(0);

    public Place() {
    }

    public Place(String name) {
        this.name = name;
    }

    public Place(String name, String description, Set<Seat> seats, Set<EventDescription> eventDescriptions) {
        this.name = name;
        this.description = description;
        this.seats = seats;
        this.eventDescriptions = eventDescriptions;
    }

    @Id
    @GeneratedValue(strategy = IDENTITY)
    @Column(name = "id", unique = true, nullable = false)
    public Integer getId() {
        return this.id;
    }

    public void setId(Integer id) {
        this.id = id;
    }

    @Column(name = "name", nullable = false)
    @NotNull
    public String getName() {
        return this.name;
    }

    public void setName(String name) {
        this.name = name;
    }

    @Column(name = "description")
    public String getDescription() {
        return this.description;
    }

    public void setDescription(String description) {
        this.description = description;
    }

    @OneToMany(fetch = FetchType.LAZY, mappedBy = "place")
    public Set<Seat> getSeats() {
        return this.seats;
    }

    public void setSeats(Set<Seat> seats) {
        this.seats = seats;
    }

    @OneToMany(fetch = FetchType.LAZY, mappedBy = "place")
    public Set<EventDescription> getEventDescriptions() {
        return this.eventDescriptions;
    }

    public void setEventDescriptions(Set<EventDescription> eventDescriptions) {
        this.eventDescriptions = eventDescriptions;
    }
}
