    private void addDirectoryToNodes(ArrayList<Pair2<String, String[]>> directory) {
        boolean expanded = false;
        for (Iterator iterator = directory.iterator(); iterator.hasNext(); ) {
            Pair2<String, String[]> entry = (Pair2<String, String[]>) iterator.next();
            DefaultMutableTreeNode strainNode = new DefaultMutableTreeNode(entry.getFirst());
            treeModel.insertNodeInto(strainNode, rootNode, treeModel.getChildCount(rootNode));
            String[] species = entry.getSecond();
            if (!expanded) {
                tree.makeVisible(new TreePath(treeModel.getPathToRoot(strainNode)));
                expanded = true;
            }
            for (String sp : species) treeModel.insertNodeInto(new DefaultMutableTreeNode(sp), strainNode, treeModel.getChildCount(strainNode));
        }
    }

    class Download implements Runnable {

        FTPClient ftp;

        public Download() {
            super();
            ftp = new FTPClient();
        }

        public void run() {
            if (currentNode == null || currentNode.equals("")) {
                JOptionPane.showMessageDialog(null, "Please select a genome to download first", "Error", JOptionPane.ERROR_MESSAGE);
                return;
            }
            String localFile = parameter.getTemporaryFilesPath() + currentNode;
            String remotePath = NCBI_FTP_PATH + currentPath;
            String remoteFile = remotePath + "/" + currentNode;
            try {
                ftp.connect(NCBI_FTP_HOST);
                int reply = ftp.getReplyCode();
                if (!FTPReply.isPositiveCompletion(reply)) {
                    ftp.disconnect();
                    JOptionPane.showMessageDialog(null, "FTP server refused connection", "Error", JOptionPane.ERROR_MESSAGE);
                }
                ftp.login("anonymous", "anonymous@big.ac.cn");
                inProgress = true;
                ftp.setFileType(FTPClient.BINARY_FILE_TYPE);
                long size = getFileSize(remotePath, currentNode);
                if (size == -1) throw new FileNotFoundException();
                BufferedOutputStream out = new BufferedOutputStream(new FileOutputStream(localFile));
                BufferedInputStream in = new BufferedInputStream(ftp.retrieveFileStream(remoteFile), ftp.getBufferSize());
                byte[] b = new byte[1024];
                long bytesTransferred = 0;
                int tick = 0;
                int oldTick = 0;
                int len;
                while ((len = in.read(b)) != -1) {
                    out.write(b, 0, len);
                    bytesTransferred += 1024;
                    if ((tick = new Long(bytesTransferred * 100 / size).intValue()) > oldTick) {
                        progressBar.setValue(tick < 100 ? tick : 99);
                        oldTick = tick;
                    }
                }
                in.close();
                out.close();
                ftp.completePendingCommand();
                progressBar.setValue(100);
                fileDownloaded = localFile;
                JOptionPane.showMessageDialog(null, "File successfully downloaded", "Congratulation!", JOptionPane.INFORMATION_MESSAGE);
                ftp.logout();
            } catch (SocketException ex) {
                JOptionPane.showMessageDialog(null, "Error occurs while trying to connect server", "Error", JOptionPane.ERROR_MESSAGE);
            } catch (FileNotFoundException ex) {
                JOptionPane.showMessageDialog(null, "This file is not found on the server", "Error", JOptionPane.ERROR_MESSAGE);
            } catch (IOException ex) {
                JOptionPane.showMessageDialog(null, "Error occurs while fetching data", "Error", JOptionPane.ERROR_MESSAGE);
            } finally {
                inProgress = false;
                if (ftp.isConnected()) {
                    try {
                        ftp.disconnect();
                    } catch (IOException ioe) {
                    }
                }
            }
        }

        private long getFileSize(String filePath, String fileName) throws IOException {
            FTPFile[] files = ftp.listFiles(filePath);
            for (FTPFile file : files) {
                if (file.getName().equals(fileName)) return file.getSize();
            }
            return -1;
        }
    }

    public String getFileDownloaded() {
        return fileDownloaded;
    }

    private void exit() {
        dispose();
        if (fileDownloaded != null && !fileDownloaded.equals("")) doc.openFileAsync(fileDownloaded, "gbk");
    }

    public void actionPerformed(ActionEvent e) {
        if (!e.getActionCommand().equals("Download")) {
            exit();
        } else {
            if (inProgress) JOptionPane.showMessageDialog(this, "Transfer already in progress", "Error", JOptionPane.ERROR_MESSAGE); else new Thread(new Download()).start();
        }
    }

    public void valueChanged(TreeSelectionEvent e) {
        TreePath leadPath = e.getNewLeadSelectionPath();
        if (leadPath != null) {
            DefaultMutableTreeNode node = (DefaultMutableTreeNode) leadPath.getLastPathComponent();
            String caption = (String) node.getUserObject();
            if (caption.endsWith("gbk")) {
                currentNode = caption;
                currentPath = (String) ((DefaultMutableTreeNode) node.getParent()).getUserObject();
            }
        }
    }

