    public String getFechaDesde() {
        String fechaDesde = "";
        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
        if (this.dateDesde.getDate() != null) {
            try {
                fechaDesde = sdf.format(this.dateDesde.getDate());
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "Error en el registro: Fecha invÃ¡lida");
            }
        }
        fechaDesde = fechaDesde + " 00:00:00";
        return fechaDesde;
    }

    public String getFechaHasta() {
        String fechaHasta = "";
        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
        if (this.dateDesde.getDate() != null) {
            try {
                fechaHasta = sdf.format(this.dateHasta.getDate());
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "Error en el registro: Fecha invÃ¡lida");
            }
        }
        fechaHasta = fechaHasta + " 23:59:59";
        return fechaHasta;
    }
