package org.txml;

/**
 * Run-time exception generated by XMLParser.parse when some kind
 * of a syntax error is encountered.
 */
public class XMLSyntaxError extends Error {

    public XMLSyntaxError(String s) {
        super("Syntax error: " + s);
    }

    public XMLSyntaxError(String s, int line) {
        this(s + " (line " + line + ")");
    }
}
