package uk.ac.ebi.rhea.domain;

import java.util.Date;

/**
 * Instances of this class contain information about a change made to a either a reaction or a compound.
 * @author rafalcan
 *
 */
public class Change {

    /**
	 * Database event generated by a change.
	 * @author rafalcan
	 * @author mzerara
	 *
	 */
    public static enum Action {

        /** Insertion */
        I("Insertion"), /** Update */
        U("Update"), /** Deletion */
        D("Deletion");

        private String label;

        private Action(String label) {
            this.label = label;
        }

        public String getLabel() {
            return label;
        }
    }

    /** change id */
    protected Long id;

    /** the action: insert, update or delete */
    protected Action action;

    /** the user name */
    protected String who;

    /** the date of change */
    protected Date when;

    /** the reason for the change */
    protected String why;

    /**
	 * Class constructor
	 * @param id the database id of the change
	 * @param action the action (insert, update or delete)
	 * @param who who did the change
	 * @param when when the change has been made
	 * @param why why the change has been made
	 */
    public Change(Long id, Action action, String who, Date when, String why) {
        this.id = id;
        this.action = action;
        this.who = who;
        this.when = when;
        this.why = why;
    }

    /**
	 * Identifies the change.
	 * @return an ID
	 */
    public Long getId() {
        return id;
    }

    /**
	 * 
	 * @return
	 */
    public Action getAction() {
        return action;
    }

    /**
	 * Identifies the user who made the change.
	 * @return a user name.
	 */
    public String getWho() {
        return who;
    }

    /**
	 * @return the {@link Date} when the change was made.
	 */
    public Date getWhen() {
        return when;
    }

    /**
	 * @return the reason given by the user for the change.
	 */
    public String getWhy() {
        return why;
    }
}
