    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {
        setModal(false);
        setVisible(false);
    }

    public class XMLFilter extends javax.swing.filechooser.FileFilter {

        public boolean accept(File f) {
            return f.getName().toLowerCase().endsWith(".xml") || f.isDirectory();
        }

        public String getDescription() {
            return "XML File";
        }
    }

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {
        chooser.setMultiSelectionEnabled(false);
        chooser.setFileFilter(new XMLFilter());
        chooser.setCurrentDirectory(new File("."));
        JFrame mainFrame = ServerApp.getApplication().getMainFrame();
        try {
            int result = chooser.showOpenDialog(mainFrame);
            jPathToVFS.setText(chooser.getSelectedFile().getPath());
        } catch (Exception e) {
        }
    }

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {
        try {
            Configuration.vfs = jPathToVFS.getText();
            Configuration.port = Integer.parseInt(jPort.getText());
            Configuration.save(path);
            setVisible(false);
        } catch (Exception e) {
            jPort.setText(Integer.toString(Configuration.port));
            jPathToVFS.setText(Configuration.vfs);
        }
    }

