package relex;

import java.util.ArrayList;

/**
 * @author miross
 * Created on Mar 29, 2005
 *
 * This class encapsulates a set of information about an input sentence
 * which is generated by Relex.  
 */
public class RelexInfo {

    public String originalSentence;

    public ArrayList<ParsedSentence> parsedSentences;

    public RelexInfo(String _originalSentence, ArrayList<ParsedSentence> _parsedSentences) {
        originalSentence = _originalSentence;
        parsedSentences = _parsedSentences;
    }
}
