package org.modss.facilitator.ui.ranking.event;

import java.util.EventObject;

/**
 * Node selection event.
 * the ranking tree has been selected.
 */
public class NodeSelectionEvent extends EventObject {

    /** Indicates whether this event was generated by the user interface. */
    boolean uiGenerated;

    /**
     * Construct a node selection event.
     */
    public NodeSelectionEvent(Object source, boolean uiGenerated) {
        super(source);
        this.uiGenerated = uiGenerated;
    }

    /**
     * Provides information about whether or not the event was caused by
     * the user interface.
     *
     * @return true if generated by the user interface, false if generated
     * by program control.
     */
    public boolean getUIGenerated() {
        return uiGenerated;
    }
}
