package com.proyecto.bigbang.core.domain;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.Id;
import javax.persistence.Table;

/**
 * Nivel generated by hbm2java
 */
@SuppressWarnings("serial")
@Entity
@Table(name = "nivel", catalog = "bigbang")
public class Nivel implements java.io.Serializable {

    private Integer idNivel;

    private String nombre;

    public Nivel() {
    }

    public Nivel(Integer idNivel) {
        this.idNivel = idNivel;
    }

    public Nivel(Integer idNivel, String nombre) {
        this.idNivel = idNivel;
        this.nombre = nombre;
    }

    @Id
    @Column(name = "idNivel", unique = true, nullable = false)
    @GeneratedValue
    public Integer getIdNivel() {
        return this.idNivel;
    }

    public void setIdNivel(Integer idNivel) {
        this.idNivel = idNivel;
    }

    @Column(name = "nombre", length = 50)
    public String getNombre() {
        return this.nombre;
    }

    public void setNombre(String nombre) {
        this.nombre = nombre;
    }

    @Override
    public String toString() {
        return "Nivel: " + this.getNombre();
    }

    @Override
    public boolean equals(Object obj) {
        if (obj instanceof Nivel) {
            Nivel unNivel = (Nivel) obj;
            if (unNivel.getIdNivel().equals(this.getIdNivel())) return true;
        }
        return false;
    }
}
