package com.skype.jsa.event;

/**
 * This listener defines all events that can be generated by a CALL object.
 * @author Bart Lamot
 *
 */
public interface CallListener extends Listener {

    void newCallReceived(CallEvent callEvent);

    void timestampUpdated(CallEvent callEvent);

    void partnerHandleUpdated(CallEvent callEvent);

    void partnerDisplaynameUpdated(CallEvent callEvent);

    void confIdUpdated(CallEvent callEvent);

    void typeUpdated(CallEvent callEvent);

    void statusUpdated(CallEvent callEvent);

    void failureReasonSet(CallEvent callEvent);

    void subjectUpdated(CallEvent callEvent);

    void pstnNumberUpdated(CallEvent callEvent);

    void durationUpdated(CallEvent callEvent);

    void pstnStatusUpdated(CallEvent callEvent);

    void confParticipantsCountUpdated(CallEvent callEvent);

    void voicemailDurationUpdated(CallEvent callEvent);

    void voicemailAllowedDurationUpdated(CallEvent callEvent);

    void seenUpdated(CallEvent callEvent);

    void dtmfUpdated(CallEvent callEvent);

    void joinConference(CallEvent callEvent);
}
