    /**
	 * get a color gradient between the start and end of colors
	 * @param num how many colors
	 * @param startColor start color
	 * @param endColor end color
	 * @return an array stores the colors
	 */
    public static Color[] getGradientColors(int num, Color startColor, Color endColor) {
        Color[] colors = new Color[num];
        for (int i = 0; i < num; i++) {
            float ratio = (float) i / (float) num;
            int red = (int) (endColor.getRed() * ratio + startColor.getRed() * (1 - ratio));
            int green = (int) (endColor.getGreen() * ratio + startColor.getGreen() * (1 - ratio));
            int blue = (int) (endColor.getBlue() * ratio + startColor.getBlue() * (1 - ratio));
            colors[i] = new Color(red, green, blue);
        }
        return colors;
    }

    public static int toGrayscale(Color color) {
        return (color.getRed() >> 2) + (color.getRed() >> 4) + (color.getGreen() >> 1) + (color.getGreen() >> 4) + (color.getBlue() >> 3);
    }

    public static String toHexCode(Color color) {
        return Utils.dec2Hex(new int[] { color.getBlue(), color.getGreen(), color.getRed() }, 2, "#");
    }
