package com.genia.toolbox.basics.bean;

/**
 * Configuration information to handle CSV files.
 */
public interface CsvConfiguration {

    /**
   * The type of the Cvs file.
   */
    public enum CsvType {

        /**
     * default type of CSV file.
     */
        DEFAULT, /**
     * CVS file generated by or for excel.
     */
        EXCEL
    }

    /**
   * getter for the commentDelimiter property.
   * 
   * @return the commentDelimiter
   */
    public abstract Character getCommentDelimiter();

    /**
   * getter for the cvsType property.
   * 
   * @return the cvsType
   */
    public abstract CsvType getCvsType();

    /**
   * getter for the delimiter property.
   * 
   * @return the delimiter
   */
    public abstract char getDelimiter();

    /**
   * getter for the encoding property.
   * 
   * @return the encoding
   */
    public abstract String getEncoding();
}
