public class TextFilterPanel extends javax.swing.JPanel {

    private FiltersWindow fw;

    /** Creates new form TextFilterPanel */
    public TextFilterPanel(FiltersWindow fw) {
        initComponents();
        this.fw = fw;
    }

    /** This method is called from within the constructor to
     * initialize the form.
     * WARNING: Do NOT modify this code. The content of this method is
     * always regenerated by the FormEditor.
     */
    private void initComponents() {
        textLabel = new javax.swing.JLabel();
        textField = new javax.swing.JTextField();
        Save = new javax.swing.JButton();
        setPreferredSize(new java.awt.Dimension(50, 100));
        setMinimumSize(new java.awt.Dimension(50, 100));
        textLabel.setText("Text:");
        add(textLabel);
        textField.setColumns(15);
        add(textField);
        Save.setText("Apply");
        Save.addActionListener(new java.awt.event.ActionListener() {

            public void actionPerformed(java.awt.event.ActionEvent evt) {
                SaveActionPerformed(evt);
            }
        });
        add(Save);
    }

    public void setText(String text) {
        textField.setText(text);
    }

    private void SaveActionPerformed(java.awt.event.ActionEvent evt) {
        String s = textField.getText();
        if ((s == null) || s.trim().equals("")) return;
        fw.saveTextFilter(s);
    }

    private javax.swing.JLabel textLabel;

    private javax.swing.JTextField textField;

    private javax.swing.JButton Save;
}
