    private void AceptarActionPerformed(java.awt.event.ActionEvent evt) {
        setCursor(Main.control.hourglassCursor);
        cambio_est = false;
        tipo.setText("");
        num.setEnabled(false);
        num.setText(null);
        modif2.setEnabled(false);
        tipo1.setEnabled(false);
        modificar.setEnabled(false);
        eliminar.setEnabled(false);
        modif.setEnabled(true);
        Aceptar1.setEnabled(true);
        int cont = tabla.getRowCount();
        for (int a = 0; a < cont; a++) {
            DefaultTableModel modelo1 = (DefaultTableModel) this.tabla.getModel();
            modelo1.removeRow(0);
        }
        int pos = clientes.getSelectedIndex();
        String[] id = (String[]) otro.get(pos);
        id_cliente = id[0];
        rif_cliente = id[1];
        nom_cliente = id[2];
        id_estado = id[3];
        direccion = id[4];
        rif.setEnabled(true);
        nombre.setEnabled(true);
        direc.setEnabled(true);
        rif.setText(rif_cliente);
        nombre.setText(nom_cliente);
        direc.setText(direccion);
        int contador = estados2.size();
        for (int i = 0; i < contador; i++) {
            String prov[] = (String[]) estados2.get(i);
            if (prov[0].equals(id_estado)) {
                estado.setText(prov[1]);
            }
        }
        Vector telf = new Vector();
        control con = new control();
        telf = con.consultar_telf(id_cliente);
        int contador2 = telf.size();
        for (int i = 0; i < contador2; i++) {
            String prov[] = (String[]) telf.get(i);
            DefaultTableModel modelo = (DefaultTableModel) this.tabla.getModel();
            Object[] fila = new Object[2];
            fila[0] = prov[0];
            fila[1] = prov[1];
            modelo.addRow(fila);
        }
        setCursor(Main.control.normalCursor);
    }

    private void clientesActionPerformed(java.awt.event.ActionEvent evt) {
    }

    private void Aceptar1ActionPerformed(java.awt.event.ActionEvent evt) {
        Vector telf = new Vector();
        int a = tabla.getRowCount();
        for (int i = 0; i < a; i++) {
            String[] arreglo_in = { "", "" };
            arreglo_in[0] = (String) tabla.getValueAt(i, 0);
            arreglo_in[1] = (String) tabla.getValueAt(i, 1);
            telf.addElement(arreglo_in);
        }
        if (cambio_est == true) {
            int pos = estados.getSelectedIndex();
            String prov[] = (String[]) otro.get(pos);
            id_estado = prov[0];
        }
        int opcion = JOptionPane.showConfirmDialog(this, "Esta seguro de los datos que esta modificando?", "Confirmacion", JOptionPane.YES_NO_OPTION);
        if (opcion == 0) {
            Vector vec = new Vector();
            control val = new control();
            vec = val.validar_rif(rif.getText());
            String id[] = (String[]) vec.get(0);
            if (id[0].equals(id_cliente) == false) {
                alertas al = new alertas();
                al.Ya_existe_cliente();
            } else {
                setCursor(Main.control.hourglassCursor);
                val.modificar_cliente(id_cliente, rif.getText(), nombre.getText(), id_estado, direc.getText(), telf);
                this.setVisible(false);
                setCursor(Main.control.normalCursor);
            }
        }
    }

    boolean cambio_est = false;

    private void modifActionPerformed(java.awt.event.ActionEvent evt) {
        control val = new control();
        est = val.consultar_estados();
        estados.setEnabled(true);
        cambio_est = true;
        int contador = est.size();
        for (int i = 0; i < contador; i++) {
            this.setVisible(true);
            String prov[] = (String[]) est.get(i);
            estados.addItem(prov[1]);
        }
    }

    boolean cambio_tipo = false;

    private void modif2ActionPerformed(java.awt.event.ActionEvent evt) {
        cambio_tipo = true;
        tipo1.setEnabled(true);
    }

    int num_selec;

    private void modificarActionPerformed(java.awt.event.ActionEvent evt) {
        cambio_tipo = false;
        tipo1.setEnabled(false);
        num_selec = tabla.getSelectedRow();
        int z = tabla.getSelectedRow();
        if (z != -1) {
            num.setEnabled(true);
            modif2.setEnabled(true);
            aceptar2.setEnabled(true);
            num.setText((String) tabla.getValueAt(z, 0));
            tipo.setText((String) tabla.getValueAt(z, 1));
        }
    }

    private void aceptar2ActionPerformed(java.awt.event.ActionEvent evt) {
        if (num.getText().isEmpty() == false) {
            if (op_escogida == 2) {
                tabla.setValueAt(num.getText(), num_selec, 0);
                if (cambio_tipo == true) {
                    tabla.setValueAt(tipo1.getSelectedItem(), num_selec, 1);
                }
                tipo1.setEnabled(false);
            } else {
                DefaultTableModel modelo = (DefaultTableModel) this.tabla.getModel();
                Object[] fila = new Object[2];
                fila[0] = num.getText();
                fila[1] = tipo1.getSelectedItem();
                modelo.addRow(fila);
            }
        } else {
            alertas al = new alertas();
            al.numero_vacio();
        }
        num.setText(null);
    }

