    private void agregar_telfActionPerformed(java.awt.event.ActionEvent evt) {
        op_escogida = 1;
        modificar.setEnabled(false);
        tipo.setText("");
        eliminar.setEnabled(false);
        num.setText(null);
        num.setEnabled(true);
        aceptar2.setEnabled(true);
        tipo1.setEnabled(true);
        modif2.setEnabled(false);
    }

    private void modificar_telfActionPerformed(java.awt.event.ActionEvent evt) {
        op_escogida = 2;
        tipo.setText("");
        modificar.setEnabled(true);
        eliminar.setEnabled(false);
        num.setEnabled(true);
        num.setText(null);
        modif2.setEnabled(true);
        aceptar2.setEnabled(true);
    }

    private void eliminar_telfActionPerformed(java.awt.event.ActionEvent evt) {
        modificar.setEnabled(false);
        eliminar.setEnabled(true);
        tipo.setText("");
        num.setEnabled(false);
        num.setText(null);
        modif2.setEnabled(false);
        tipo1.setEnabled(false);
        aceptar2.setEnabled(false);
    }

    private void eliminarActionPerformed(java.awt.event.ActionEvent evt) {
        int z = tabla.getSelectedRow();
        if (z != -1) {
            DefaultTableModel modelo1 = (DefaultTableModel) this.tabla.getModel();
            modelo1.removeRow(z);
        }
    }

