package org.yaffa.commons.exceptioning;

import org.yaffa.commons.messages.Messages;

/**
 * 
 * @author zgobolos.at.sf.net
 *
 */
public class TechnicalException extends RuntimeException implements YaffaException {

    /** Serial version UID generated by Eclipse. */
    private static final long serialVersionUID = -6861021365647983407L;

    /**
	 * 
	 * @param packageName
	 * @param key
	 * @param cause
	 */
    public TechnicalException(String packageName, String key, Throwable cause) {
        super(Messages.getString(packageName, key), cause);
    }
}
