package GUI.GUIAuthors;

import java.awt.GridLayout;
import java.awt.event.MouseEvent;
import javax.swing.ImageIcon;
import javax.swing.JButton;
import javax.swing.JFrame;
import javax.swing.JLabel;
import javax.swing.JPanel;

/**
 * Conference information shown in the form of a picture.
 * It's content should be generated by the conference holder.
 * @author  G1
 */
public class ConferenceInfo extends JFrame {

    private static final long serialVersionUID = 1L;

    private JButton button;

    private JLabel label;

    private JPanel panel;

    /**
	 * Class contructor.
	 * Creates new form ConferenceInfo
	 */
    public ConferenceInfo() {
        button = new javax.swing.JButton("Back");
        button.addMouseListener(new java.awt.event.MouseAdapter() {

            public void mouseClicked(java.awt.event.MouseEvent evt) {
                jButton1MouseClicked(evt);
            }
        });
        this.setSize(900, 700);
        this.setLayout(new GridLayout(1, 1));
        ImageIcon image = createImageIcon("/GUI/GUIAuthors/imgs/ConferenceInfo.JPG");
        label = new JLabel(image);
        panel = new JPanel();
        panel.add(label);
        panel.add(button);
        this.add(panel);
    }

    private void jButton1MouseClicked(MouseEvent evt) {
        this.dispose();
    }

    private ImageIcon createImageIcon(String path) {
        java.net.URL imgURL = getClass().getResource(path);
        if (imgURL != null) {
            return new ImageIcon(imgURL);
        } else {
            System.err.println("Couldn't find file: " + path);
            return null;
        }
    }

    public static void main(String args[]) {
        java.awt.EventQueue.invokeLater(new Runnable() {

            public void run() {
                new ConferenceInfo().setVisible(true);
            }
        });
    }
}
