package com.scheublegroup.icon;

/**
 * @author c.scheuble
 */
class IconEntryMask extends IndexedIconEntry {

    /**
     * Generated by eclipse.
     */
    private static final long serialVersionUID = 7266220830951925004L;

    /**
     * Create a new icon entry bitmap mask.
     * <p>
     * Override the super constructor so another bitmap mask is not created.
     * 
     * @param parentEntry Parent icon entry.
     */
    IconEntryMask(final IIconEntry parentEntry) {
        super(parentEntry);
        getDescriptor().setBitsPerPixel(1);
        getHeader().setBitsPerPixel(1);
    }

    /**
     * Check if the mask indicates that the pixel is transparent.
     * 
     * @param xPos Horizontal coordinate.
     * @param yPos Vertical coordinate.
     * @return true if the mask indicates that the pixel is transparent.
     */
    public boolean isOpaque(final int xPos, final int yPos) {
        return getPaletteIndex(xPos, yPos) == 0;
    }
}
