    private void guessButtonActionPerformed(java.awt.event.ActionEvent evt) {
        try {
            String s = JOptionPane.showInputDialog(null, "Enter Your Guess Please!", "Enter a number", JOptionPane.INFORMATION_MESSAGE);
            guessedNumber.setText(s);
        } catch (NumberFormatException nfe) {
            JOptionPane.showMessageDialog(null, "Only enter a number", "You did not enter a number", JOptionPane.ERROR_MESSAGE);
        }
    }

