    private void jButtonConcluirActionPerformed(java.awt.event.ActionEvent evt) {
        FacadeRnaRBF.setNeuroniosCamadaOculta(Integer.parseInt(jTextFieldNroNeuronios.getText()));
        FacadeRnaRBF.setFuncaoAtivacao((String) jComboBoxFuncaoAtivacao.getSelectedItem());
        FacadeRnaRBF.setTxAprendizado(Double.parseDouble(jFormattedTextFieldTxAprendizado.getText()));
        FacadeRnaRBF.setLarguraAtivacao((String) jComboBoxLarguraAtivacao.getSelectedItem());
        FacadeRnaRBF.setEpocas(Integer.parseInt(jFormattedTextFieldEpocas.getText()));
        FacadeRnaRBF.setErroMinimo(Double.parseDouble(jFormattedTextFieldErroMinimo.getText()));
        FacadeRnaRBF.getJDialogConfig().dispose();
    }

