    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {
        String hombre;
        if (ophombre.isSelected()) {
            hombre = "Hombre";
        } else hombre = "Mujer";
        if (GestionEmpleado.verificampo(this.jTextApellidos1, this.jTextNombre, this.jTextApellidos, this.jTextMail, this.jTextIdentificacion)) {
            if (!ophombre.isSelected() && !opmujer.isSelected()) {
                mensaje.setText("digite el sexo");
            } else {
                if (DIA.getSelectedIndex() == 0 || MES.getSelectedIndex() == 0 || ANO.getSelectedIndex() == 0 || fechavalida < 0) {
                    mensaje.setText("ingrese una fecha valida");
                } else {
                    if (fechavalida <= 17) {
                        System.out.print("\n" + fechavalida);
                        mensaje.setText("El empleaddo es Menor de Edad");
                    } else {
                        int otro = MES.getSelectedIndex();
                        System.out.print("\n" + fechavalida);
                        if (GestionEmpleado.validador.validaNumero(jTextIdentificacion, 2) && GestionEmpleado.validador.validaEmail(jTextMail) && GestionEmpleado.validador.validaNumero(jTextTelefono, 11) && GestionEmpleado.validador.validaNumero(jTextCelular, 11)) {
                            try {
                                if (ControladorEmpleado.BuscarEmpleado(jTextIdentificacion.getText()) == null) RegistraEmplado(hombre, otro); else jTextIdentificacion.requestFocus();
                                jTextIdentificacion.setSelectionColor(Color.red);
                                jTextIdentificacion.selectAll();
                                mensaje.setText("Identificacion ya Existe");
                            } catch (SQLException ex) {
                                Logger.getLogger(RegistrarEmpleado.class.getName()).log(Level.SEVERE, null, ex);
                            }
                        } else {
                            mensaje.setText("Digite los Campos correctamente");
                        }
                    }
                }
            }
        } else {
            mensaje.setText("Llene todos los campos");
        }
    }

