    @Override
    protected String validateContents(Component component, Object event) {
        try {
            double gridSize = Double.parseDouble(jTextField1.getText());
            if (gridSize < Double.parseDouble((String) getWizardData("d0")) * 1.4) {
                return "Grid step must be bigger than d0 * 1.4";
            } else {
                return null;
            }
        } catch (NumberFormatException ex) {
            return "Invalid grid step";
        }
    }

