package edu.isi.misd.image.gateway.conversion.util;

import java.io.File;

/**
 * Utilites used by Zoomify tools.
 * 
 * @author David Smith
 * 
 */
public class ZoomifyUtils {

    private static final String THUMB_TILE = "TileGroup0" + File.separator + "0-0-0.jpg";

    /**
     * 
     * @param tileDir
     *            the tile directory
     * @return the name of the thumbnail generated by Zoomify for the given tile
     *         directory.
     */
    public static final String getThumbnailFilename(final String tileDir) {
        if (tileDir == null || tileDir.length() == 0) {
            throw new IllegalArgumentException("Tile directory must be specified.");
        }
        return tileDir + File.separator + THUMB_TILE;
    }
}
