    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {
        testHarness.fileParser f = new testHarness.fileParser();
        caller.mem = f.parseHex(jTextField1.getText(), jTextField1.getText().length());
        caller.j.p.keyAddress = caller.mem;
        if (jRadioButton1.isSelected()) caller.irq = 0; else if (jRadioButton2.isSelected()) caller.irq = 1; else if (jRadioButton3.isSelected()) caller.irq = 2;
        button b = l.get(caller.listLoc);
        b.mem = caller.mem;
        b.irq = caller.irq;
        b.ID = caller.ID;
        l.set(caller.listLoc, b);
        this.setVisible(false);
    }

