package org.nescent.phenoscape.orb.persistentdomain;

import java.util.HashSet;
import java.util.Set;

/**
 * Ontology generated by hbm2java
 */
public class Ontology implements java.io.Serializable {

    private long ontologyId;

    private String ontologyName;

    private String ontologyPrefix;

    private String ontologyDomain;

    private Set<OntologyGatekeeper> ontologyGatekeepers = new HashSet<OntologyGatekeeper>(0);

    private Set<TermOntology> termOntologies = new HashSet<TermOntology>(0);

    public Ontology() {
    }

    public Ontology(long ontologyId, String ontologyName, String ontologyPrefix) {
        this.ontologyId = ontologyId;
        this.ontologyName = ontologyName;
        this.ontologyPrefix = ontologyPrefix;
    }

    public Ontology(long ontologyId, String ontologyName, String ontologyPrefix, String ontologyDomain, Set<OntologyGatekeeper> ontologyGatekeepers, Set<TermOntology> termOntologies) {
        this.ontologyId = ontologyId;
        this.ontologyName = ontologyName;
        this.ontologyPrefix = ontologyPrefix;
        this.ontologyDomain = ontologyDomain;
        this.ontologyGatekeepers = ontologyGatekeepers;
        this.termOntologies = termOntologies;
    }

    public long getOntologyId() {
        return this.ontologyId;
    }

    public void setOntologyId(long ontologyId) {
        this.ontologyId = ontologyId;
    }

    public String getOntologyName() {
        return this.ontologyName;
    }

    public void setOntologyName(String ontologyName) {
        this.ontologyName = ontologyName;
    }

    public String getOntologyPrefix() {
        return this.ontologyPrefix;
    }

    public void setOntologyPrefix(String ontologyPrefix) {
        this.ontologyPrefix = ontologyPrefix;
    }

    public String getOntologyDomain() {
        return this.ontologyDomain;
    }

    public void setOntologyDomain(String ontologyDomain) {
        this.ontologyDomain = ontologyDomain;
    }

    public Set<OntologyGatekeeper> getOntologyGatekeepers() {
        return this.ontologyGatekeepers;
    }

    public void setOntologyGatekeepers(Set<OntologyGatekeeper> ontologyGatekeepers) {
        this.ontologyGatekeepers = ontologyGatekeepers;
    }

    public Set<TermOntology> getTermOntologies() {
        return this.termOntologies;
    }

    public void setTermOntologies(Set<TermOntology> termOntologies) {
        this.termOntologies = termOntologies;
    }
}
