    private void veriGoster() {
        try {
            danisman.setText("");
            ogrenciAdi.setText(result.getString("adi"));
            ogrenciNo.setText(result.getString("ogrenciNo"));
            kararNoField.setText(result.getString("kararNo"));
            String tarih = result.getString("tarih");
            String abd = result.getString("abd");
            tarihField.setText(tarih);
            dilekceNoField.setText(result.getString("dilekceNo"));
            dilekceTarihiField.setText(result.getString("dilekceTarihi"));
            String programTipi = result.getString("tur");
            if (programTipi.contentEquals("Tezli YL")) egitimTuru.setSelectedIndex(0); else if (programTipi.contentEquals("Tezsiz YL")) egitimTuru.setSelectedIndex(1); else if (programTipi.contentEquals("DR")) egitimTuru.setSelectedIndex(2); else if (programTipi.contentEquals("Bütünleşik DR")) egitimTuru.setSelectedIndex(3); else if (programTipi.contentEquals("2.Öğr.Tezsiz YL")) egitimTuru.setSelectedIndex(4);
            String statu = result.getString("statu");
            if (statu.contains("Burslu")) {
                statuCombo.setSelectedIndex(0);
            } else if (statu.contains("Arş.")) {
                statuCombo.setSelectedIndex(1);
            } else {
                statuCombo.setSelectedIndex(2);
            }
            yonetmelikmadde.setText(result.getString("yonetmelikmadde"));
            String program = result.getString("bolum");
            int abdIndex = abdComboModel.getIndexOf(abd);
            abdCombo.setSelectedIndex(abdIndex);
            int programIndex = programComboModel.getIndexOf(program);
            programCombo.setSelectedIndex(programIndex);
            if (result.getInt("yabanci") == 1) {
                yabanciRadio.setSelected(true);
            } else {
                tcRadio.setSelected(true);
            }
            String danismanResult = result.getString("danisman");
            if (danismanResult.contains("Prof.Dr.")) {
                danismanUnvan.setSelectedIndex(0);
                danisman.setText(danismanResult.substring(8));
            } else if (danismanResult.contains("Yrd.Doç.Dr.")) {
                danismanUnvan.setSelectedIndex(2);
                danisman.setText(danismanResult.substring(11));
            } else if (danismanResult.contains("Doç.Dr.")) {
                danismanUnvan.setSelectedIndex(1);
                danisman.setText(danismanResult.substring(7));
            }
            String danismanAbd = result.getString("danismanAbd");
            int abdIndex2 = abdComboModel2.getIndexOf(danismanAbd);
            abdCombo2.setSelectedIndex(abdIndex2);
            String danismanResult2 = result.getString("danisman2");
            if (danismanResult2.contains("Prof.Dr.")) {
                danismanUnvan2.setSelectedIndex(0);
                danisman2.setText(danismanResult2.substring(8));
            } else if (danismanResult2.contains("Yrd.Doç.Dr.")) {
                danismanUnvan2.setSelectedIndex(2);
                danisman2.setText(danismanResult2.substring(11));
            } else if (danismanResult2.contains("Doç.Dr.")) {
                danismanUnvan2.setSelectedIndex(1);
                danisman2.setText(danismanResult2.substring(7));
            }
            String danismanAbd2 = result.getString("danismanAbd2");
            int abdIndex3 = abdComboModel3.getIndexOf(danismanAbd2);
            abdCombo3.setSelectedIndex(abdIndex3);
            String danismanResult3 = result.getString("danisman3");
            if (danismanResult3.contains("Prof.Dr.")) {
                danismanUnvan3.setSelectedIndex(0);
                danisman3.setText(danismanResult3.substring(8));
            } else if (danismanResult3.contains("Yrd.Doç.Dr.")) {
                danismanUnvan3.setSelectedIndex(2);
                danisman3.setText(danismanResult3.substring(11));
            } else if (danismanResult3.contains("Doç.Dr.")) {
                danismanUnvan3.setSelectedIndex(1);
                danisman3.setText(danismanResult3.substring(7));
            }
            String danismanAbd3 = result.getString("danismanAbd3");
            int abdIndex4 = abdComboModel4.getIndexOf(danismanAbd3);
            abdCombo4.setSelectedIndex(abdIndex4);
            if (result.getInt("danismanEmekli") == 1) {
                emekliCheck.setSelected(true);
            } else {
                emekliCheck.setSelected(false);
            }
            if (result.getInt("danismanEmekli2") == 1) {
                emekliCheck2.setSelected(true);
            } else {
                emekliCheck2.setSelected(false);
            }
            if (result.getInt("danismanEmekli3") == 1) {
                emekliCheck3.setSelected(true);
            } else {
                emekliCheck3.setSelected(false);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e.getMessage());
        }
    }

