    private void formWindowOpened(java.awt.event.WindowEvent evt) {
        try {
            Baglanti.createConnection();
            ResultSet abd = Baglanti.runQuery("SELECT isim FROM APP.ABDALI ORDER BY isim");
            while (abd.next()) {
                abdComboModel.addElement(abd.getObject(1));
                abdComboModel2.addElement(abd.getObject(1));
                abdComboModel3.addElement(abd.getObject(1));
                abdComboModel4.addElement(abd.getObject(1));
            }
            abdComboActionPerformed(null);
            abdCombo2ActionPerformed(null);
            abdCombo3ActionPerformed(null);
            abdCombo4ActionPerformed(null);
            StringBuffer query = new StringBuffer();
            query.append("SELECT * FROM APP.TEZIZLEMEKOMITESI");
            query.append(" WHERE toplantiSayisi=" + toplantiSayisi);
            Baglanti.createConnection();
            result = Baglanti.runQuery(query.toString());
            if (result.next()) veriGoster(); else {
                yeni.doClick();
            }
            tcRadio.setSelected(true);
            tarihField.setText(toplantiTarihi);
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(rootPane, "Hata:" + e.getMessage());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Hatha:" + e.getMessage());
        }
    }

