package spacewalklib;

/**
 *
 * Exception thrown by spacewalklib library in case of system fails to execute
 * scripts.
 *
 * @author Alfredo Moralejo
 *
 */
public class RhnExecutionException extends Exception {

    /**
     *  Creates a new exception with the supplied message and error code.
     *  The message should include the error generated by XMLRPC library.
     *
     *  @param message The exception message.
     */
    public RhnExecutionException(String message) {
        super(message);
    }

    /** Serial version UID. */
    private static final long serialVersionUID = 1136432499557456882L;
}
