    private static void printUsage(PrintStream out) {
        out.print("Usage: gogui-convert infile outfile\n" + "\n" + "-check   only check reading a file\n" + "-config  config file\n" + "-force   overwrite existing files\n" + "-format  output format (sgf,tex,xml)\n" + "-help    display this help and exit\n" + "-title   use title\n" + "-version print version and exit\n" + "-werror  handle read warnings as errors\n");
    }
