    private InputStream getSrc2Source() {
        String contents = "/* Example of an swrtj program by Fabio Strocco */\n" + "\n" + "import \"Fifo.swrtj\"\n" + "\n" + "interface IReader {\n" + "	IString readString(IString message);\n" + "}\n" + "\n" + "class CReader implements IReader by {} and { //Direct anonimous trait definition\n" + "	IString readString(IString message) { //Provided method\n" + "		out.println(message);\n" + "		return in.nextLine();\n" + "	}\n" + "}\n" + "{\n" + "	CReader() {}\n" + "}\n" + "\n" + "program Main {\n" + "	IReader reader = new CReader();\n" + "	IQueue queue = new Queue();\n" + "	IFifo fifo = new Fifo();\n" + "		\n" + "	while(!reader.readString(\"Type ENTER to insert an element or EXIT to terminate the program\")\n" + "	             .toLowerCase().equals(\"exit\")) {\n" + "		IString element = reader.readString(\"Insert a string\");\n" + "		queue.enqueue(element);\n" + "		fifo.enqueue(element);\n" + "	}\n" + "	\n" + "	while(!fifo.isEmpty()) {\n" + "		out.println(\"Queue dequeue = \".concat((IString)(queue.dequeue())));\n" + "		out.println(\"Fifo peek = \".concat((IString)(fifo.peek())));\n" + "		out.println(\"Fifo dequeue\");\n" + "		fifo.dequeue();\n" + "	}\n" + "	\n" + "	out.println(\"Program terminated\");	\n" + "}\n";
        return new ByteArrayInputStream(contents.getBytes());
    }
