    void writeMacrosHydrogenBond() throws IOException {
        out("#macro hbond1(X1,Y1,Z1,X2,Y2,Z2,RADIUS,R,G,B)\n" + "#local dx = (X2 - X1) / 10;\n" + "#local dy = (Y2 - Y1) / 10;\n" + "#local dz = (Z2 - Z1) / 10;\n" + " cylinder{<X1+dx  ,Y1+dy  ,Z1+dz  >,<X1+3*dx,Y1+3*dy,Z1+3*dz>,RADIUS\n" + "  pigment{rgb<R,G,B>}}\n" + " cylinder{<X1+4*dx,Y1+4*dy,Z1+4*dz>,<X2-4*dx,Y2-4*dy,Z2-4*dz>,RADIUS\n" + "  pigment{rgb<R,G,B>}}\n" + " cylinder{<X2-3*dx,Y2-3*dy,Z2-3*dz>,<X2-dx  ,Y2-dy  ,Z2-dz  >,RADIUS\n" + "  pigment{rgb<R,G,B>}}\n" + "#end\n\n");
        out("#macro hbond2(X1,Y1,Z1,X2,Y2,Z2,RADIUS,R1,G1,B1,R2,G2,B2)\n" + "#local dx = (X2 - X1) / 10;\n" + "#local dy = (Y2 - Y1) / 10;\n" + "#local dz = (Z2 - Z1) / 10;\n" + "#local xc = (X1 + X2) / 2;\n" + "#local yc = (Y1 + Y2) / 2;\n" + "#local zc = (Z1 + Z2) / 2;\n" + " cylinder{<X1+dx  ,Y1+dy  ,Z1+dz  >,<X1+3*dx,Y1+3*dy,Z1+3*dz>,RADIUS\n" + "  pigment{rgb<R1,G1,B1>}}\n" + " cylinder{<X1+4*dx,Y1+4*dy,Z1+4*dz>,<xc     ,yc     ,zc     >,RADIUS\n" + "  pigment{rgb<R1,G1,B1>}}\n" + " cylinder{<xc     ,yc     ,zc     >,<X2-4*dx,Y2-4*dy,Z2-4*dz>,RADIUS\n" + "  pigment{rgb<R2,G2,B2>}}\n" + " cylinder{<X2-3*dx,Y2-3*dy,Z2-3*dz>,<X2-dx  ,Y2-dy  ,Z2-dz  >,RADIUS\n" + "  pigment{rgb<R2,G2,B2>}}\n" + "#end\n\n");
    }
